	public static String longestCommonPrefix(String[] strs) {
        if(strs.length == 0)    return "";
        
        ArrayList<String> common = new ArrayList<String>();
        
        //Add all possible prefixes of 1st string to start off
        char[] chArr0 = strs[0].toCharArray();
        if(chArr0.length == 0)   return "";
        
        StringBuilder sb0 = new StringBuilder();
        for(int i=0; i< chArr0.length; i++)  {
            sb0.append(chArr0[i]);
            common.add(sb0.toString());
        }
        if(strs.length == 1)	return common.size()> 0? common.get(common.size() -1): "";
        
        int j=0;
        for(int i=1; i< strs.length && common.size() > 0; i++)   {
            if(common.contains(strs[i]))    { 
                j = strs[i].length();
                common = removeCommon(common, j);
                continue;   //When the whole string is present, it is not going to alter common
            }
            StringBuilder sb = new StringBuilder();
            char[] chArr = strs[i].toCharArray();
            if(chArr.length == 0)   return "";
            
            for(j=0; j< chArr.length; j++)  {
                sb.append(chArr[j]);
                if(!common.contains(sb.toString())) { 
                	common = removeCommon(common, j);
                	break;
                }
            }
        }
        return common.size()> 0? common.get(common.size()-1): "";
    }
    
    public static ArrayList<String> removeCommon(ArrayList<String> commonPrefixes, int stopPoint)    {
        for(int i=stopPoint; i< commonPrefixes.size();) {
            commonPrefixes.remove(i);
        }
        return commonPrefixes;
    }

